function devNum = findKeyboardDevNumsAtLocation(location)% devNum = findKeyboardDevNumsAtLocation(location)%% find the device numbers for the keyboard and the for the keypad...% 11/17/07 DE% 31-March-2011 Rachel Denison%% Updated to take location argument% 2012 September 1devices = PsychHID('Devices');devNum.Keyboard = []; devNum.Keypad = [];for devicecounter = 1:length(devices)    switch location        case 'laptop'            if (strcmp(devices(devicecounter).product, 'Apple Internal Keyboard / Trackpad') == 1) && (strcmp(devices(devicecounter).usageName, 'Keyboard') == 1)                devNum.Keypad = devicecounter;            end        case 'melbourne'            if (strcmp(devices(devicecounter).product, 'Apple Internal Keyboard / Trackpad') == 1) && (strcmp(devices(devicecounter).usageName, 'Keyboard') == 1)                devNum.Keypad = devicecounter;            end        otherwise            error('Testing location not found by findKeyboardDevNumsAtLocation.')    endend